$("#quantity_change_enter").on("click",function(){const t=parseInt($("#source_quantity").val()),r=parseInt($("#target_quantity").val());try{if($("#source_item_code").val()==$("#target_item_code").val())throw new Error("交換元と交換先が同じ商品になっています。");if(t<1||isNaN(t))throw new Error("交換元の数量が正しくありません。");if(r<1||isNaN(r))throw new Error("交換先の数量が正しくありません。");if(r<=t)throw new Error("交換先の数量が交換元の数量より大きくありません。");window.confirm("交換処理を実行しますか？")==!0&&$("#quantity_change_form").submit()}catch(e){alert(e.message)}});$("#source_quantity").on("change",function(){const t=parseInt($("#source_quantity").val());!isNaN(t)&&t>0&&$("#target_quantity").val(t*2)});
